const uniqueEmojis = new Set(data.split(' ').filter((a) => a.length < 3))

function shuffleArray(array: any[]) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1))
    const temp = array[i]
    array[i] = array[j]
    array[j] = temp
  }
}

export default Vue.extend({
  name: 'GameBoard',
  props: {
    tiles: { type: Number, default: 40 }
  },
  computed: {
    chips(): string[] {
      const data = [...uniqueEmojis]
      shuffleArray(data)
      return data.slice(0, this.tiles)
    },
    selected(): string[] {
      const set = new Set<string>()
      while (set.size < 5) {
        set.add(this.chips[Math.floor(Math.random() * this.chips.length)])
      }
      return [...set];
    },
    /* todo calc tile layout according to display */
    tileWidth(): number {
      // todo get default from cache
      if (!this.$el) return 100;
      return this.$el.clientWidth / 8;
    }
  },
  mounted() {
    this.$emit("mounted", this.selected);
  },
  methods: {
    selectEmoji(emoji: string) {
      const index = this.selected.indexOf(emoji);
      if (index >= 0) this.$emit("correct", emoji);
    }
  }
})
